import List from "./List";
import ListAdd from "./ListAdd";
export default {
  components: {
    List: List,
    ListAdd: ListAdd
  },
  data: function data() {
    return {
      todolist: [],
      date: null
    };
  },
  computed: {
    doneList: function doneList() {
      var count = 0;
      this.todoList.forEach(function (e) {
        if (e.status === 'done') count++;
      });
      return count;
    }
  },
  methods: {
    listAdd: function listAdd(memo, date) {
      console.log('받음');
      this.todolist.push({
        memo: memo,
        status: "created",
        date: date
      });
    },
    listEdit: function listEdit(memo, index) {
      this.todolist[index].memo = memo;
    },
    statusControl: function statusControl(index, status) {
      console.log("".concat(index, ",").concat(status));
      this.todolist[index].status = status;
    },
    listDelete: function listDelete(index) {
      this.todolist.splice(index, 1);
    }
  }
};
